import gurobipy as gp
from gurobipy import GRB

# Example data for a job-shop scheduling problem
# Machines: m1, m2
# Jobs: j1, j2
# Each job is a sequence of operations (machine, processing time)
jobs = {
    1: [(1, 3), (2, 2)],  # Job 1: op1 on m1 (3), op2 on m2 (2)
    2: [(2, 2), (1, 4)]   # Job 2: op1 on m2 (2), op2 on m1 (4)
}
machines = set()
for ops in jobs.values():
    for m, _ in ops:
        machines.add(m)
machines = sorted(machines)

# Build operation list: (job, op_idx) -> (machine, proc_time)
operations = []
for i in jobs:
    for j, (m, p) in enumerate(jobs[i]):
        operations.append((i, j))

# For each machine, collect all operations on it
machine_ops = {m: [] for m in machines}
for i in jobs:
    for j, (m, p) in enumerate(jobs[i]):
        machine_ops[m].append((i, j))

# Big-M
M = 1000

m = gp.Model("JobShop_Scheduling")

# Decision variables
S = m.addVars(operations, lb=0, vtype=GRB.CONTINUOUS, name="S")  # Start times
Cmax = m.addVar(lb=0, vtype=GRB.CONTINUOUS, name="Cmax")

# Precedence constraints within jobs
for i in jobs:
    n_ops = len(jobs[i])
    for k in range(n_ops - 1):
        m.addConstr(
            S[i, k+1] >= S[i, k] + jobs[i][k][1],
            name=f"job_prec_{i}_{k}"
        )

# Disjunctive constraints for operations on the same machine
for mach in machines:
    ops = machine_ops[mach]
    for idx1 in range(len(ops)):
        for idx2 in range(len(ops)):
            if idx1 == idx2:
                continue
            a, b = ops[idx1]
            c, d = ops[idx2]
            if (a, b) < (c, d):  # Only one direction to avoid duplicates
                X = m.addVar(vtype=GRB.BINARY, name=f"X_{a}_{b}_{c}_{d}")
                # S_{a,b} after S_{c,d}
                m.addConstr(
                    S[a, b] >= S[c, d] + jobs[c][d][1] - M * (1 - X),
                    name=f"mach_disj1_{a}_{b}_{c}_{d}"
                )
                # S_{c,d} after S_{a,b}
                m.addConstr(
                    S[c, d] >= S[a, b] + jobs[a][b][1] - M * X,
                    name=f"mach_disj2_{a}_{b}_{c}_{d}"
                )

# Cmax constraints
for i in jobs:
    for j, (mach, p) in enumerate(jobs[i]):
        m.addConstr(
            Cmax >= S[i, j] + p,
            name=f"cmax_{i}_{j}"
        )

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

m.optimize()

import gurobipy as gp
from gurobipy import GRB

# Sets
jobs = range(1, 7)         # j = 1..6
machines = [1, 2]          # k = 1,2
tasks = [(j, k) for j in jobs for k in machines]

# Model
m = gp.Model("Minimize_Cmax_Scheduling")

# Decision variables
S = m.addVars(tasks, lb=0, vtype=GRB.CONTINUOUS, name="S")  # Start times
Cmax = m.addVar(lb=0, vtype=GRB.CONTINUOUS, name="Cmax")

# For each machine, collect the jobs assigned to it
machine_tasks = {k: [(j, k) for j in jobs] for k in machines}

# y variables for ordering on each machine
y = {}
for k in machines:
    for i in jobs:
        for i2 in jobs:
            if i != i2:
                y[(i, k), (i2, k)] = m.addVar(vtype=GRB.BINARY, name=f"y_{i}_{k}_{i2}_{k}")

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

# Constraints

# 1. S_{j,2} >= S_{j,1} + 1 for all j
for j in jobs:
    m.addConstr(S[j,2] >= S[j,1] + 1, name=f"precedence_{j}")

# 2. Cmax >= S_{j,k} + 1 for all j,k
for j in jobs:
    for k in machines:
        m.addConstr(Cmax >= S[j,k] + 1, name=f"cmax_lb_{j}_{k}")

# 3. Disjunctive constraints for jobs on the same machine
M = 100000
for k in machines:
    for i in jobs:
        for i2 in jobs:
            if i != i2:
                # S_{i,k} >= S_{i2,k} + 1 - M*(1 - y_{(i,k),(i2,k)})
                m.addConstr(S[i,k] >= S[i2,k] + 1 - M * (1 - y[(i, k), (i2, k)]), name=f"disj1_{i}_{i2}_{k}")
                # S_{i2,k} >= S_{i,k} + 1 - M*y_{(i,k),(i2,k)}
                m.addConstr(S[i2,k] >= S[i,k] + 1 - M * y[(i, k), (i2, k)], name=f"disj2_{i}_{i2}_{k}")
                # y_{(i,k),(i2,k)} + y_{(i2,k),(i,k)} == 1
                m.addConstr(y[(i, k), (i2, k)] + y[(i2, k), (i, k)] == 1, name=f"order_{i}_{i2}_{k}")

# Solve
m.optimize()

import gurobipy as gp
from gurobipy import GRB
import numpy as np

# Sets
products = range(3)  # i = 0,1,2
months = range(1, 7) # t = 1..6

# Example data (replace with actual data as needed)
c = np.array([
    [4, 5, 6, 4, 5, 6],   # c_{0,t}
    [3, 4, 5, 3, 4, 5],   # c_{1,t}
    [2, 3, 4, 2, 3, 4]    # c_{2,t}
])
s = np.array([
    [100, 100, 100, 100, 100, 100], # s_{0,t}
    [120, 120, 120, 120, 120, 120], # s_{1,t}
    [110, 110, 110, 110, 110, 110]  # s_{2,t}
])
h = np.array([
    [1, 1, 1, 1, 1, 1],   # h_{0,t}
    [2, 2, 2, 2, 2, 2],   # h_{1,t}
    [1, 1, 1, 1, 1, 1]    # h_{2,t}
])
d = np.array([
    [20, 30, 25, 35, 30, 40], # d_{0,t}
    [15, 25, 20, 30, 25, 35], # d_{1,t}
    [10, 20, 15, 25, 20, 30]  # d_{2,t}
])
r = [2, 3, 1]                # r_i
R = [100, 100, 100, 100, 100, 100] # R_t
U = np.array([
    [50, 50, 50, 50, 50, 50], # U_{0,t}
    [40, 40, 40, 40, 40, 40], # U_{1,t}
    [60, 60, 60, 60, 60, 60]  # U_{2,t}
])

# Model
m = gp.Model("Production_Planning")

# Decision variables
x = m.addVars(products, months, vtype=GRB.INTEGER, lb=0, name="x")
y = m.addVars(products, months, vtype=GRB.BINARY, name="y")
I = m.addVars(products, range(0, 7), vtype=GRB.INTEGER, lb=0, name="I") # I_{i,0} to I_{i,6}

# Objective
m.setObjective(
    gp.quicksum(
        c[i, t-1] * x[i, t] + s[i, t-1] * y[i, t] + h[i, t-1] * I[i, t]
        for i in products for t in months
    ),
    GRB.MINIMIZE
)

# Initial inventory constraints
for i in products:
    m.addConstr(I[i, 0] == 0, name=f"init_inventory_{i}")

# Final inventory constraints
for i in products:
    m.addConstr(I[i, 6] == 0, name=f"final_inventory_{i}")

# Inventory balance constraints
for i in products:
    for t in months:
        m.addConstr(
            I[i, t] == I[i, t-1] + x[i, t] - d[i, t-1],
            name=f"inv_balance_{i}_{t}"
        )

# Resource capacity constraints
for t in months:
    m.addConstr(
        gp.quicksum(r[i] * x[i, t] for i in products) <= R[t-1],
        name=f"resource_{t}"
    )

# Production setup constraints
for i in products:
    for t in months:
        m.addConstr(
            x[i, t] <= U[i, t-1] * y[i, t],
            name=f"setup_{i}_{t}"
        )

# Solve
m.optimize()

import gurobipy as gp
from gurobipy import GRB

# Example data for a job shop scheduling problem
# Jobs, each with a list of (machine, operation index)
jobs = {
    1: [(1, 1), (2, 2), (3, 3)],
    2: [(2, 1), (1, 2), (3, 3)],
    3: [(3, 1), (2, 2), (1, 3)]
}
# Processing times for each operation (all 1 for this model)
proc_time = 1

# Machines
machines = set()
for ops in jobs.values():
    for m, _ in ops:
        machines.add(m)
machines = sorted(machines)

# List of all operations: (job, op_idx, machine)
operations = []
for j, ops in jobs.items():
    for o, (m, _) in enumerate(ops, 1):
        operations.append((j, o, m))

# For each machine, list of operations on it
machine_ops = {m: [] for m in machines}
for j, ops in jobs.items():
    for o, (m, _) in enumerate(ops, 1):
        machine_ops[m].append((j, o))

# Big-M
M = 10000

m = gp.Model("JobShop_MinCmax")

# Decision variables
S = m.addVars([(j, o) for (j, o, _) in operations], vtype=GRB.INTEGER, lb=0, name="S")
Cmax = m.addVar(vtype=GRB.INTEGER, lb=0, name="Cmax")
# For each machine, for each pair of different operations on it, define X
X = {}
for mach in machines:
    ops = machine_ops[mach]
    for i in range(len(ops)):
        for j in range(len(ops)):
            if i != j:
                X[(ops[i], ops[j])] = m.addVar(vtype=GRB.BINARY, name=f"X_{ops[i]}_{ops[j]}")

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

# Technological order constraints: S_{j,o+1} >= S_{j,o} + 1
for j, ops in jobs.items():
    for o in range(1, len(ops)):
        m.addConstr(S[j, o+1] >= S[j, o] + proc_time, name=f"tech_order_{j}_{o}")

# Disjunctive constraints for operations on the same machine
for mach in machines:
    ops = machine_ops[mach]
    for i in range(len(ops)):
        for j in range(len(ops)):
            if i != j:
                (j1, o1) = ops[i]
                (j2, o2) = ops[j]
                if (j1, o1) != (j2, o2):
                    # S_{j1,o1} >= S_{j2,o2} + 1 - M(1 - X_{(j1,o1),(j2,o2)})
                    m.addConstr(
                        S[j1, o1] >= S[j2, o2] + proc_time - M * (1 - X[( (j1, o1), (j2, o2) )]),
                        name=f"disj1_{j1}_{o1}_{j2}_{o2}"
                    )
                    # S_{j2,o2} >= S_{j1,o1} + 1 - M X_{(j1,o1),(j2,o2)}
                    m.addConstr(
                        S[j2, o2] >= S[j1, o1] + proc_time - M * X[( (j1, o1), (j2, o2) )],
                        name=f"disj2_{j1}_{o1}_{j2}_{o2}"
                    )
    # For each unordered pair, add X_{a,b} + X_{b,a} = 1
    for i in range(len(ops)):
        for j in range(i+1, len(ops)):
            a = ops[i]
            b = ops[j]
            m.addConstr(X[(a, b)] + X[(b, a)] == 1, name=f"order_{a}_{b}")

# Cmax constraints: Cmax >= S_{j,last} + 1 for all jobs
for j, ops in jobs.items():
    last_op = len(ops)
    m.addConstr(Cmax >= S[j, last_op] + proc_time, name=f"cmax_{j}")

m.optimize()

import gurobipy as gp
from gurobipy import GRB

# Parameters
n_jobs = 7
operations = [1, 2]
jobs = range(1, n_jobs + 1)
ops = operations

# For demonstration, assume all jobs share the same machine for both operations
# so all pairs (j,o) != (k,p) are in the same machine
# In practice, you would define which (j,o) pairs share a machine

M = 1000  # Big-M value, should be set large enough

m = gp.Model("General_Job_Shop_Makespan")

# Decision variables
S = m.addVars(jobs, ops, lb=0, vtype=GRB.CONTINUOUS, name="S")  # Start times
Cmax = m.addVar(lb=0, vtype=GRB.CONTINUOUS, name="Cmax")

# y_{j,o,k,p} = 1 if (j,o) precedes (k,p) on the machine
y = {}
for j in jobs:
    for o in ops:
        for k in jobs:
            for p in ops:
                if (j, o) != (k, p):
                    y[j, o, k, p] = m.addVar(vtype=GRB.BINARY, name=f"y_{j}_{o}_{k}_{p}")

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

# Technological constraints: S_{j,2} >= S_{j,1} + 1
for j in jobs:
    m.addConstr(S[j, 2] >= S[j, 1] + 1, name=f"tech_{j}")

# Cmax constraints: Cmax >= S_{j,o} + 1
for j in jobs:
    for o in ops:
        m.addConstr(Cmax >= S[j, o] + 1, name=f"cmax_{j}_{o}")

# Disjunctive constraints for all pairs (j,o) != (k,p) that share a machine
for j in jobs:
    for o in ops:
        for k in jobs:
            for p in ops:
                if (j, o) != (k, p):
                    # Only one of the two can be true
                    m.addConstr(S[j, o] >= S[k, p] + 1 - M * (1 - y[j, o, k, p]), name=f"disj1_{j}_{o}_{k}_{p}")
                    m.addConstr(S[k, p] >= S[j, o] + 1 - M * y[j, o, k, p], name=f"disj2_{j}_{o}_{k}_{p}")
                    m.addConstr(y[j, o, k, p] + y[k, p, j, o] == 1, name=f"order_{j}_{o}_{k}_{p}")

m.optimize()

import gurobipy as gp
from gurobipy import GRB

# Sets
I = range(3)  # i = 0,1,2
M = range(2)  # m = 0,1
T = range(3)  # t = 0,1,2

# Data
d = {
    (0, 0): 21.53845915883999, (0, 1): 22.99877365370049, (0, 2): 20.41750946364395,
    (1, 0): 24.34292430654904, (1, 1): 18.69161579278047, (1, 2): 16.96075239636408,
    (2, 0): 15.44135005337596, (2, 1): 24.75403844533352, (2, 2): 17.09921582696376
}
s = {0: 6.900949355420833, 1: 7.255877688483005}
cap = {0: 73.62985121550206, 1: 69.3191618930891}
M_big = 1000  # Sufficiently large constant

# Objective coefficients
c_y = 145.2319299071881
c_z = 65.79360965435782
c_I = {0: 0.8652264753415143, 1: 0.9341662877492469, 2: 1.07339072521956}
c_B = {0: 7.607410261300208, 1: 6.920344391340704, 2: 6.497781941350707}

# Model
m = gp.Model("Complex_MIP")

# Decision variables
x = m.addVars(I, M, T, lb=0, name="x")
y = m.addVars(I, M, T, vtype=GRB.BINARY, name="y")
z = m.addVars(I, M, T, vtype=GRB.BINARY, name="z")
Ivar = m.addVars(I, T, lb=0, name="I")
B = m.addVars(I, T, lb=0, name="B")

# Objective
obj = gp.quicksum(
    c_y * y[i, m, t] + c_z * z[i, m, t]
    for i in I for m in M for t in T
) + gp.quicksum(
    c_I[i] * Ivar[i, t] for i in I for t in T
) + gp.quicksum(
    c_B[i] * B[i, t] for i in I for t in T
)
m.setObjective(obj, GRB.MINIMIZE)

# Constraints

# Inventory balance constraints
for i in I:
    for t in T:
        lhs = gp.quicksum(x[i, m, t] for m in M)
        if t == 0:
            m.addConstr(lhs == d[i, t] + Ivar[i, t] - B[i, t], name=f"invbal_{i}_{t}")
        else:
            m.addConstr(lhs + Ivar[i, t-1] - B[i, t-1] == d[i, t] + Ivar[i, t] - B[i, t], name=f"invbal_{i}_{t}")

# Capacity constraints
for m in M:
    for t in T:
        m.addConstr(
            gp.quicksum(x[i, m, t] for i in I) + gp.quicksum(s[m] * z[i, m, t] for i in I) <= cap[m],
            name=f"cap_{m}_{t}"
        )

# At most one y per machine per period
for m in M:
    for t in T:
        m.addConstr(
            gp.quicksum(y[i, m, t] for i in I) <= 1,
            name=f"one_y_{m}_{t}"
        )

# Linking x and y
for i in I:
    for m in M:
        for t in T:
            m.addConstr(
                x[i, m, t] <= M_big * y[i, m, t],
                name=f"link_xy_{i}_{m}_{t}"
            )

# Linking z and y (startup)
for i in I:
    for m in M:
        # t=0
        m.addConstr(
            z[i, m, 0] == y[i, m, 0],
            name=f"z0_{i}_{m}"
        )
        # t=1,2
        for t in [1, 2]:
            m.addConstr(
                z[i, m, t] >= y[i, m, t] - y[i, m, t-1],
                name=f"z_{i}_{m}_{t}"
            )

# Solve
m.optimize()

import gurobipy as gp
from gurobipy import GRB

# Sets
M = [0, 1]  # machines
P = [0, 1]  # products
T = [0, 1, 2]  # periods

# Parameters
C = {0: 76.7045484622153, 1: 69.3695364951939}
d = {
    (0, 0): 22.92965094851524, (0, 1): 19.03396834281949, (0, 2): 18.90167422155575,
    (1, 0): 19.80185049682801, (1, 1): 24.22810173621122, (1, 2): 16.84556169689551
}
cost_y = 149.1695690337089
cost_s = 69.24269269143792
cost_I = {0: 1.080318154052025, 1: 1.143069947055348}
cost_B = {0: 6.798895023015501, 1: 6.372651501311341}
M_big = 1000  # sufficiently large

# Model
m = gp.Model("Production_Scheduling")

# Decision variables
x = m.addVars(M, P, T, lb=0, name="x")  # production
y = m.addVars(M, P, T, vtype=GRB.BINARY, name="y")  # setup
s = m.addVars(M, P, T, vtype=GRB.BINARY, name="s")  # startup
I = m.addVars(P, T, lb=0, name="I")  # inventory
B = m.addVars(P, T, lb=0, name="B")  # backlog

# Objective
obj = gp.quicksum(cost_y * y[m_, p, t] for m_ in M for p in P for t in T) \
    + gp.quicksum(cost_s * s[m_, p, t] for m_ in M for p in P for t in T) \
    + gp.quicksum(cost_I[p] * I[p, t] for p in P for t in T) \
    + gp.quicksum(cost_B[p] * B[p, t] for p in P for t in T)
m.setObjective(obj, GRB.MINIMIZE)

# Constraints

# Demand balance
for p in P:
    for t in T:
        lhs = gp.quicksum(x[m_, p, t] for m_ in M)
        if t == 0:
            m.addConstr(lhs == d[p, t] + I[p, t] - B[p, t], name=f"demand_balance_p{p}_t{t}")
        else:
            m.addConstr(lhs + I[p, t-1] - B[p, t-1] == d[p, t] + I[p, t] - B[p, t], name=f"demand_balance_p{p}_t{t}")

# Machine capacity
for m_ in M:
    for t in T:
        m.addConstr(
            gp.quicksum(x[m_, p, t] for p in P) + gp.quicksum(s[m_, p, t] for p in P) <= C[m_],
            name=f"capacity_m{m_}_t{t}"
        )

# One product per machine per period
for m_ in M:
    for t in T:
        m.addConstr(gp.quicksum(y[m_, p, t] for p in P) <= 1, name=f"one_product_m{m_}_t{t}")

# Link production to setup
for m_ in M:
    for p in P:
        for t in T:
            m.addConstr(x[m_, p, t] <= M_big * y[m_, p, t], name=f"prod_setup_m{m_}_p{p}_t{t}")

# Startup logic
for m_ in M:
    for p in P:
        for t in T:
            if t == 0:
                m.addConstr(s[m_, p, t] >= y[m_, p, t], name=f"startup0_m{m_}_p{p}_t{t}")
            else:
                m.addConstr(s[m_, p, t] >= y[m_, p, t] - y[m_, p, t-1], name=f"startup_m{m_}_p{p}_t{t}")

# Solve
m.optimize()

import gurobipy as gp
from gurobipy import GRB

# Parameters
n_jobs = 7
operations = [1, 2]
jobs = range(1, n_jobs + 1)
ops = operations

# For this code, we assume all operations are assigned to the same machine
# (as the constraints are only for pairs on the same machine)
# If you have machine assignment, you need to add that logic.

bigM = 100000

m = gp.Model("Minimize_Cmax_Scheduling")

# Decision variables
S = m.addVars(jobs, ops, lb=0, vtype=GRB.CONTINUOUS, name="S")  # Start times
Cmax = m.addVar(lb=0, vtype=GRB.CONTINUOUS, name="Cmax")

# y[(j,o),(j',o')] = 1 if (j,o) precedes (j',o'), 0 otherwise
y = {}
for j in jobs:
    for o in ops:
        for jp in jobs:
            for op in ops:
                if (j, o) != (jp, op):
                    y[(j, o), (jp, op)] = m.addVar(vtype=GRB.BINARY, name=f"y_{j}_{o}_{jp}_{op}")

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

# Constraints

# Each job's operation 2 starts after operation 1 + 1
for j in jobs:
    m.addConstr(S[j, 2] >= S[j, 1] + 1, name=f"op_order_{j}")

# Disjunctive constraints for all pairs of operations (on the same machine)
for j in jobs:
    for o in ops:
        for jp in jobs:
            for op in ops:
                if (j, o) != (jp, op):
                    # Only one of (j,o) before (jp,op) or vice versa
                    m.addConstr(y[(j, o), (jp, op)] + y[(jp, op), (j, o)] == 1, name=f"disj_{j}_{o}_{jp}_{op}")
                    # If (j,o) before (jp,op)
                    m.addConstr(S[jp, op] >= S[j, o] + 1 - bigM * (1 - y[(j, o), (jp, op)]), name=f"precede1_{j}_{o}_{jp}_{op}")
                    # If (jp,op) before (j,o)
                    m.addConstr(S[j, o] >= S[jp, op] + 1 - bigM * y[(j, o), (jp, op)], name=f"precede2_{j}_{o}_{jp}_{op}")

# Cmax constraints
for j in jobs:
    for o in ops:
        m.addConstr(Cmax >= S[j, o] + 1, name=f"Cmax_{j}_{o}")

m.optimize()

import gurobipy as gp
from gurobipy import GRB
import numpy as np

# Sets
M = [0, 1]      # Machines
P = [0, 1, 2]   # Products
T = [0, 1, 2]   # Periods

# Parameters
c_x = 143.7514410203047
