# Variables
S = m.addVars(jobs, lb=0, vtype=GRB.CONTINUOUS, name="S")  # Start times
Cmax = m.addVar(lb=0, vtype=GRB.CONTINUOUS, name="Cmax")

# y_{ik} for jobs on the same machine, i != k
y = {}
for mach in machines:
    mach_jobs = machines[mach]
    for i in mach_jobs:
        for k in mach_jobs:
            if i != k:
                y[i, k] = m.addVar(vtype=GRB.BINARY, name=f"y_{i}_{k}")

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

# Constraints

# Precedence: S_{j,2} >= S_{j,1} + 1 for j=1..7
for j in range(1, 8):
    m.addConstr(S[j + 7] >= S[j] + 1, name=f"precedence_{j}")

# Cmax >= S_i + 1 for all i
for i in jobs:
    m.addConstr(Cmax >= S[i] + 1, name=f"cmax_{i}")

# Disjunctive constraints for jobs on the same machine
M = 100000
for mach in machines:
    mach_jobs = machines[mach]
    for i in mach_jobs:
        for k in mach_jobs:
            if i != k:
                m.addConstr(S[i] >= S[k] + 1 - M * (1 - y[i, k]), name=f"disj1_{i}_{k}")
                m.addConstr(S[k] >= S[i] + 1 - M * y[i, k], name=f"disj2_{i}_{k}")

