Cmax = m.addVar(lb=0, vtype=GRB.CONTINUOUS, name="Cmax")

# For each stage, define y_{ij} for i < j (disjunctive constraints)
y = {}
for k in stages:
    for i in jobs:
        for j in jobs:
            if i < j:
                y[i, j, k] = m.addVar(vtype=GRB.BINARY, name=f"y_{i}_{j}_stage{k}")

# Objective
m.setObjective(Cmax, GRB.MINIMIZE)

# Stage precedence constraints: S_{j,2} >= S_{j,1} + 1
for j in jobs:
    m.addConstr(S[j, 2] >= S[j, 1] + 1, name=f"stage_prec_{j}")

# Disjunctive constraints for each stage (no overlap on same machine)
M = 100000
for k in stages:
    for i in jobs:
        for j in jobs:
            if i < j:
                # Either i before j or j before i on stage k
                m.addConstr(S[j, k] >= S[i, k] + 1 - M * (1 - y[i, j, k]), name=f"disj1_{i}_{j}_stage{k}")
                m.addConstr(S[i, k] >= S[j, k] + 1 - M * y[i, j, k], name=f"disj2_{i}_{j}_stage{k}")

# Makespan constraints: Cmax >= S_{j,k} + 1 for all jobs and stages
for j in jobs:
    for k in stages:
        m.addConstr(Cmax >= S[j, k] + 1, name=f"makespan_{j}_{k}")

# Solve
