import gurobipy as gp
from gurobipy import GRB
import numpy as np

# Index sets
E = range(7)      # e = 0..6
R = range(2)      # r = 0,1
S = range(2)      # s = 0,1
K = range(1)      # k = 0

# Example data (replace with your actual data)
# c_{e,r,s,k}
