# a_{e,s}
a = np.random.randint(0, 2, size=(7, 2))
# q_{e,k}
q = np.ones((7, 1), dtype=int)
# d_{r,s,k}
d = np.random.randint(1, 4, size=(2, 2, 1))

m = gp.Model("Custom_Optimization_Model")

# Decision variables
x = m.addVars(E, R, S, K, vtype=GRB.BINARY, name="x")
u = m.addVars(R, S, K, vtype=GRB.INTEGER, lb=0, name="u")

# Objective function
m.setObjective(
    gp.quicksum(
        c[e, r, s, k] * x[e, r, s, k] + 100 * u[r, s, k]
        for e in E for r in R for s in S for k in K
    ),
    GRB.MINIMIZE
)

# Constraint 1: Demand satisfaction
for r in R:
    for s in S:
        for k in K:
            m.addConstr(
                gp.quicksum(x[e, r, s, k] for e in E) + u[r, s, k] == d[r, s, k],
                name=f"demand_{r}_{s}_{k}"
            )

# Constraint 2: Assignment upper bound
for e in E:
    for r in R:
        for s in S:
            for k in K:
                m.addConstr(
                    x[e, r, s, k] <= a[e, s] * q[e, k],
                    name=f"assign_ub_{e}_{r}_{s}_{k}"
                )

# Constraint 3: Each e can be assigned at most once
for e in E:
    m.addConstr(
        gp.quicksum(x[e, r, s, k] for r in R for s in S for k in K) <= 1,
        name=f"unique_e_{e}"
    )

m.optimize()

